import { useState } from "react";
import TodoList from "./TodoList";
import FormInput from "./FormInput";

export default function () {
  const [todos, setTodos] = useState([]); //used for storing todo data
  return (
    <div>
      <FormInput setTodos={setTodos} todos={todos}/>

      {todos.map((todo) => (
          <TodoList key={todo} item={todo} />
        ))}
    </div>
  );
}
