import { useState } from "react";

export default function () {
  const [todo, setTodo] = useState(""); //used for input values
  const [todos, setTodos] = useState([]); //used for storing todo data

  function handleSubmit(e) {
    e.preventDefault();
    setTodos(todo);
  }
  return (
    <div>
      <form onSubmit={(e) => handleSubmit(e)}>
        <input
          onChange={(e) => setTodo(e.target.value)}
          value={todo}
          type="text"
        />
        <button type="submit">ADD</button>
      </form>
    </div>
  );
}
