import React, { useState } from "react";
import { Link } from "react-router-dom";
import Validation from "./LoginValidation";

function Login() {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });
  const [errors, setErrors] = useState({});

  const handleSubmit = (event) => {
    event.preventDefault();
    setErrors(setValues(Validation(values)));
  };
  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: [event.target.values],
    }));
  };
  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-warning">
      <div className="bg-white p-3 rounded w-25">
        <form action="" onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="email">Email</label>
            <input
              onChange={handleInput}
              type="email"
              placeholder="Enter email"
              className="form-control rounded-0"
              name="email"
            />
            {errors.email && <span>{errors.email}</span>}
          </div>
          <div className="mb-3">
            <label htmlFor="password">Password</label>
            <input
              onChange={handleInput}
              type="password"
              placeholder="Enter password"
              className="form-control rounded-0"
              name="password"
            />
          </div>
          <button type="submit" className="btn btn-success mb-3 w-100">
            Login
          </button>
          <Link to="/signup" className="btn btn-default border w-100">
            Creat Account
          </Link>
        </form>
      </div>
    </div>
  );
}

export default Login;
