import { useState } from "react";
import TodoList from "./TodoList";
import FormInput from "./FormInput";

export default function () {
  const [todo, setTodo] = useState(""); //used for input values
  const [todos, setTodos] = useState([]); //used for storing todo data

  function handleSubmit(e) {
    e.preventDefault();
    setTodos([...todos,todo]);
    setTodo("")
  }
  return (
    <div>
      <FormInput setTodo={setTodo} />

      {todos.map((todo) => (
          <TodoList key={todo} item={todo} />
        ))}
    </div>
  );
}
