import React, { useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import style from "./Headerfile.module.css";

function Header() {
  const history = useHistory();

  useEffect(() => {
    const isAuthenticated = localStorage.getItem("username");
    if (!isAuthenticated) {
      history.push("/login"); // Redirect to login page if not authenticated
    }
  }, []);

  const handleLogout = () => {
    // Clear local storage
    localStorage.removeItem("username");
    // Redirect to login page
    history.push("/login");
  };

  return (
    <div className={style.header}>
      <div className={style.users}>Hello {localStorage.getItem("username")}</div>
      <div className={style.titles}>TODO LIST</div>
      <div className={style.logoutBtn}>
        <button onClick={handleLogout}>Log Out</button>
      </div>
    </div>
  );
}

export default Header;
